// ============================================
// PATTERN: Success Handler with Trace Propagation
// ============================================

// Get original config with _trace
const config = $('Entry Config').first().json;
const response = $json;

let message = '';
try {
  message = response.candidates[0].content.parts[0].text || '';
  message = message.trim().substring(0, 200);
} catch(e) {
  message = 'Hey ' + config.student_name + '! Keep your ' + config.current_streak + '-day streak alive!';
}

// Update trace with completion info
const trace = {
  ...config._trace,
  completed_at: new Date().toISOString(),
  path: 'success',
  ai_model: 'gemini-2.5-flash'
};

return {
  success: true,
  notification: {
    title: 'Streak Alert',
    body: message
  },
  student: {
    id: config.student_id,
    name: config.student_name,
    streak: config.current_streak
  },
  ai_used: true,

  // Include trace in response
  _trace: trace
};

// ============================================
// PATTERN: Error/Fallback Handler with Trace
// ============================================

// Get original config with _trace
const config = $('Entry Config').first().json;
const fallback = 'Hey ' + config.student_name + '! Your ' + config.current_streak + '-day streak is amazing. Keep it going!';

// Update trace with error info
const trace = {
  ...config._trace,
  completed_at: new Date().toISOString(),
  path: 'fallback',
  error_reason: 'ai_call_failed'
};

return {
  success: true,
  notification: {
    title: 'Streak Alert',
    body: fallback
  },
  student: {
    id: config.student_id,
    name: config.student_name,
    streak: config.current_streak
  },
  ai_used: false,

  // Include trace in response
  _trace: trace
};

