// PATTERN: Entry Point with Correlation ID
// ============================================
const body = $json.body || $json;

const correlationId = body.correlation_id || 
  'corr_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);

const trace = {
  correlation_id: correlationId,
  workflow_id: $workflow.id,
  workflow_name: $workflow.name,
  execution_id: $execution.id,
  started_at: new Date().toISOString()
};

return {
  student_id: body.student_id,
  student_name: body.student_name,
  current_streak: body.current_streak,
  preferred_subject: body.preferred_subject,
  _trace: trace,
  _raw: body
};

// ============================================
// PATTERN: Input Validation
// ============================================
const data = $json;
const errors = [];

const schema = {
  required: ['student_id', 'student_name', 'current_streak'],
  properties: {
    student_id: { type: 'string', minLength: 1, maxLength: 50 },
    student_name: { type: 'string', minLength: 1, maxLength: 100 },
    current_streak: { type: 'number', minimum: 0, maximum: 9999 },
    preferred_subject: { type: 'string', maxLength: 50 }
  }
};

for (const field of schema.required) {
  if (data[field] === undefined || data[field] === null || data[field] === '') {
    errors.push('Missing required field: ' + field);
  }
}

const props = schema.properties;
if (data.student_id && typeof data.student_id !== 'string') errors.push('student_id must be string');
if (data.student_name && typeof data.student_name !== 'string') errors.push('student_name must be string');
if (data.current_streak !== undefined && isNaN(Number(data.current_streak))) errors.push('current_streak must be number');

if (errors.length > 0) {
  return {
    _validation_failed: true,
    success: false,
    error: { code: 'VALIDATION_ERROR', message: 'Input validation failed', details: errors },
    _trace: { ...data._trace, completed_at: new Date().toISOString(), path: 'validation_error' }
  };
}

return {
  student_id: String(data.student_id).trim().substring(0, 50),
  student_name: String(data.student_name).trim().substring(0, 100),
  current_streak: Math.max(0, Math.min(9999, Number(data.current_streak))),
  preferred_subject: String(data.preferred_subject || 'studies').trim().substring(0, 50),
  _trace: data._trace,
  _validation_passed: true
};

