// ============================================
// PATTERN: Success + Cost Tracking
// ============================================
// Gemini 2.5 Flash pricing (as of Dec 2024):
// Input: $0.15 per 1M tokens, Output: $0.60 per 1M tokens
// With thinking: Input $0.15, Thinking $1.25, Output $0.60 per 1M

const PRICING = {
  model: 'gemini-2.5-flash',
  input_per_million: 0.15,
  output_per_million: 0.60,
  thinking_per_million: 1.25,
  currency: 'USD'
};

const config = $('Validate Input').first().json;
const response = $json;

// Extract usage metadata from Gemini response
let usageMetadata = {};
try {
  usageMetadata = response.usageMetadata || {};
} catch(e) {
  usageMetadata = { promptTokenCount: 0, candidatesTokenCount: 0, totalTokenCount: 0 };
}

// Calculate costs
const inputTokens = usageMetadata.promptTokenCount || 0;
const outputTokens = usageMetadata.candidatesTokenCount || 0;
const thinkingTokens = usageMetadata.thoughtsTokenCount || 0;
const totalTokens = usageMetadata.totalTokenCount || (inputTokens + outputTokens + thinkingTokens);

const inputCost = (inputTokens / 1000000) * PRICING.input_per_million;
const outputCost = (outputTokens / 1000000) * PRICING.output_per_million;
const thinkingCost = (thinkingTokens / 1000000) * PRICING.thinking_per_million;
const totalCost = inputCost + outputCost + thinkingCost;

// Parse message
let message = '';
try {
  message = response.candidates[0].content.parts[0].text || '';
  message = message.trim().substring(0, 200);
} catch(e) {
  message = 'Hey ' + config.student_name + '! Keep your ' + config.current_streak + '-day streak alive!';
}

// Build cost tracking object
const cost = {
  model: PRICING.model,
  tokens: {
    input: inputTokens,
    output: outputTokens,
    thinking: thinkingTokens,
    total: totalTokens
  },
  cost_usd: {
    input: Number(inputCost.toFixed(8)),
    output: Number(outputCost.toFixed(8)),
    thinking: Number(thinkingCost.toFixed(8)),
    total: Number(totalCost.toFixed(8))
  },
  pricing_reference: PRICING
};

const trace = {
  ...config._trace,
  completed_at: new Date().toISOString(),
  path: 'success',
  ai_model: PRICING.model
};

return {
  success: true,
  notification: { title: 'Streak Alert', body: message },
  student: { id: config.student_id, name: config.student_name, streak: config.current_streak },
  ai_used: true,
  _cost: cost,
  _trace: trace
};

// ============================================
// PATTERN: Fallback with Zero Cost
// ============================================
const config = $('Validate Input').first().json;
const errorInfo = $json.error || {};
const fallback = 'Hey ' + config.student_name + '! Your ' + config.current_streak + '-day streak is amazing. Keep it going!';

// No AI cost on fallback
const cost = {
  model: 'none',
  tokens: { input: 0, output: 0, thinking: 0, total: 0 },
  cost_usd: { input: 0, output: 0, thinking: 0, total: 0 },
  fallback_reason: errorInfo.message || 'ai_call_failed'
};

const trace = {
  ...config._trace,
  completed_at: new Date().toISOString(),
  path: 'ai_fallback',
  error_reason: errorInfo.message || 'ai_call_failed'
};

return {
  success: true,
  notification: { title: 'Streak Alert', body: fallback },
  student: { id: config.student_id, name: config.student_name, streak: config.current_streak },
  ai_used: false,
  _cost: cost,
  _trace: trace
};
